
using ScriptPortal.Vegas;
using System;

namespace LiveLineLogoRemover
{
    public static class CustomRenderTemplates
    {
        private static RenderTemplate CreateTemplate(string templateData)
        {
            string renderName = "MAGIX AVC/AAC MP4";
            Renderer renderer = EntryPoint.Vegas.Renderers.FindByName(renderName);

            var bytes = Convert.FromBase64String(templateData);
            return renderer.CreateRenderTemplate(bytes);
        }

        public static RenderTemplate FullHD_30fps_10mbps => CreateTemplate("");
        public static RenderTemplate FullHD_60fps_10mbps => CreateTemplate("");
    }
}